const featuredProductsContainer = document.getElementById('featured-products');

featuredProductsContainer.addEventListener('click', e => {
  if (e.target.classList.contains('view-details')) {
    const id = parseInt(e.target.dataset.id);
    openProductModal(id);
  }
});


document.addEventListener('DOMContentLoaded', renderFeaturedProducts);


  const productsContainer = document.getElementById('products');
  const cartCount = document.getElementById('cart-count');
  const productModal = document.getElementById('product-modal');
  const closeProductModalBtn = document.getElementById('close-product-modal');
  const modalImage = document.getElementById('modal-image');
  const modalTitle = document.getElementById('modal-title');
  const modalPrice = document.getElementById('modal-price');
  const modalDescription = document.getElementById('modal-description');
  const quantityInput = document.getElementById('quantity');
  const quantityMinusBtn = document.getElementById('quantity-minus');
  const quantityPlusBtn = document.getElementById('quantity-plus');
  const addToCartBtn = document.getElementById('add-to-cart-btn');

  const cartModal = document.getElementById('cart-modal');
  const closeCartModalBtn = document.getElementById('close-cart-modal');
  const cartItemsContainer = document.getElementById('cart-items');
  const emptyCartMessage = document.getElementById('empty-cart');
  const cartTotal = document.getElementById('cart-total');
  const continueShoppingBtn = document.getElementById('continue-shopping');
  const checkoutBtn = document.getElementById('checkout-btn');

  const cartIcon = document.getElementById('cart-icon');

  let currentProduct = null;


  function displayProducts() {
    productsContainer.innerHTML = '';
    products.forEach(product => {
      const col = document.createElement('div');
      col.className = 'col-md-4';
      col.innerHTML = `
        <div class="product-card d-flex flex-column h-100">
          <img src="${product.image}" alt="${product.alt}" class="mb-3 rounded" />
          <h5>${product.title}</h5>
          <p class="text-primary fw-bold">$${product.price.toFixed(2)}</p>
          <p class="flex-grow-1">${product.description.substring(0, 80)}...</p>
          <button class="btn btn-outline-primary mt-auto view-details" data-id="${product.id}">View Details</button>
        </div>
      `;
      productsContainer.appendChild(col);
    });
  }
  
  function openProductModal(productId) {
    currentProduct = products.find(p => p.id === productId);
    if (!currentProduct) return;

    modalImage.src = currentProduct.image;
    modalImage.alt = currentProduct.alt;
    modalTitle.textContent = currentProduct.title;
    modalPrice.textContent = `$${currentProduct.price.toFixed(2)}`;
    modalDescription.textContent = currentProduct.description;
    quantityInput.value = 1;

    productModal.style.display = 'block';
  }

  function closeProductModal() {
    productModal.style.display = 'none';
    currentProduct = null;
  }

  function updateCartCount() {
    const totalCount = cart.reduce((sum, item) => sum + item.quantity, 0);
    cartCount.textContent = totalCount;
  }


  function saveCart() {
    localStorage.setItem('cart', JSON.stringify(cart));
  }


  function addToCart() {
    if (!currentProduct) return;

    const qty = parseInt(quantityInput.value);
    if (isNaN(qty) || qty < 1) {
      alert('Please enter a valid quantity (1 or more).');
      return;
    }

    const existingItem = cart.find(item => item.id === currentProduct.id);
    if (existingItem) {
      existingItem.quantity += qty;
    } else {
      cart.push({ ...currentProduct, quantity: qty });
    }

    saveCart();
    updateCartCount();
    alert(`Added ${qty} x "${currentProduct.title}" to cart.`);
    closeProductModal();
  }
  

  function renderCart() {
    cartItemsContainer.innerHTML = '';

    if (cart.length === 0) {
      emptyCartMessage.style.display = 'block';
      cartTotal.textContent = 'Total: $0.00';
      checkoutBtn.disabled = true;
      return;
    } else {
      emptyCartMessage.style.display = 'none';
      checkoutBtn.disabled = false;
    }

    let totalPrice = 0;

    cart.forEach(item => {
      totalPrice += item.price * item.quantity;

      const itemDiv = document.createElement('div');
      itemDiv.className = 'cart-item d-flex justify-content-between align-items-center';

      itemDiv.innerHTML = `
        <div>
          <h6>${item.title}</h6>
          <p>$${item.price.toFixed(2)} x ${item.quantity} = $${(item.price * item.quantity).toFixed(2)}</p>
        </div>
        <div>
          <button class="btn btn-sm btn-outline-secondary me-1 decrease-qty" data-id="${item.id}">-</button>
          <button class="btn btn-sm btn-outline-secondary me-1 increase-qty" data-id="${item.id}">+</button>
          <button class="btn btn-sm btn-danger remove-item" data-id="${item.id}">Remove</button>
        </div>
      `;

      cartItemsContainer.appendChild(itemDiv);
    });

    cartTotal.textContent = `Total: $${totalPrice.toFixed(2)}`;
  }

  function openCartModal() {
    renderCart();
    cartModal.style.display = 'block';
  }

  function closeCartModal() {
    cartModal.style.display = 'none';
  }

  function changeQuantity(productId, delta) {
    const item = cart.find(i => i.id === productId);
    if (!item) return;

    item.quantity += delta;             
    if (item.quantity < 1) {
      cart = cart.filter(i => i.id !== productId);
    }
    saveCart();
    updateCartCount();
    renderCart();
  }

  function removeItem(productId) {
    cart = cart.filter(i => i.id !== productId);
    saveCart();
    updateCartCount();
    renderCart();
  }


  function setupEventListeners() {

    productsContainer.addEventListener('click', e => {
      if (e.target.classList.contains('view-details')) {
        const id = parseInt(e.target.dataset.id);
        openProductModal(id);
      }
    });

    closeProductModalBtn.addEventListener('click', closeProductModal);

    quantityMinusBtn.addEventListener('click', () => {
      let val = parseInt(quantityInput.value);
      if (val > 1) quantityInput.value = val - 1;
    });
    quantityPlusBtn.addEventListener('click', () => {
      let val = parseInt(quantityInput.value);
      quantityInput.value = val + 1;
    });

    addToCartBtn.addEventListener('click', addToCart);

    cartIcon.addEventListener('click', openCartModal);

    closeCartModalBtn.addEventListener('click', closeCartModal);

    window.addEventListener('click', e => {
      if (e.target === productModal) closeProductModal();
      if (e.target === cartModal) closeCartModal();
    });

    cartItemsContainer.addEventListener('click', e => {
      const id = parseInt(e.target.dataset.id);
      if (e.target.classList.contains('increase-qty')) {
        changeQuantity(id, +1);
      } else if (e.target.classList.contains('decrease-qty')) {
        changeQuantity(id, -1);
      } else if (e.target.classList.contains('remove-item')) {
        removeItem(id);
      }
    });

    continueShoppingBtn.addEventListener('click', closeCartModal);

    checkoutBtn.addEventListener('click', () => {
      window.location.href = 'checkout.html';
    });
  }

  
  function init() {
    displayProducts();
    updateCartCount();
    setupEventListeners();
  }
  
  document.addEventListener('DOMContentLoaded', init);
